mapper.select:
SELECT

FROM petition_contradiction
WHERE
delete_flag=0


      AND id IN

          #{item}




          AND id = #{queryParam.id,jdbcType=BIGINT}


          AND apply_name IN

              #{item}



          AND responsible_company IN

              #{item}



          AND apply_sex = #{queryParam.applySex,jdbcType=VARCHAR}


          AND check_type = #{queryParam.checkType,jdbcType=BIT}


          AND team_petition_state = #{queryParam.teamPetitionState,jdbcType=BIT}


          AND resolve_level = #{queryParam.resolveLevel,jdbcType=VARCHAR}



              AND petition_type LIKE concat("%", #{item}, "%")



          AND purpose_type = #{queryParam.purposeType,jdbcType=VARCHAR}


          AND contradiction_type LIKE concat("%", #{queryParam.contradictionType}, "%")



ORDER BY insert_on
